import { makeAutoObservable } from "mobx";

class CanvasState {
   canvas = null
   redo = []
   undo = []

   constructor() {
      makeAutoObservable(this)
   }

   setCanvas(canvas) {
      this.canvas = canvas
   }

   pushToRedo (data) {
      this.redo.push(data)
   }

   pushToUndo (data) {
      this.undo.push(data)
   }
}

export default new CanvasState()
