import React from "react";
import { observer } from "mobx-react-lite";
import "../style/canvas.scss";
import canvasState from "../store/canvasState";
import toolState from "../store/toolState";
import Brush from "../tools/Brush";
import { Modal, Button } from "react-bootstrap";
import { useParams } from "react-router-dom";

const Canvas = observer(() => {
  const canvasRef = React.useRef();
  const usernameInput = React.useRef();
  const [visibleModal, setVisibleModal] = React.useState(true);
  const params = useParams();

  React.useEffect(() => {
    canvasState.setCanvas(canvasRef.current);
    toolState.setTool(new Brush(canvasRef.current));
  }, []);

  React.useEffect(() => {
    if (canvasState.username) {
      const ws = new WebSocket("ws://localhost:5000/");
      canvasState.setWSocket(ws);
      canvasState.setSession(params.id);
      ws.onopen = () => {
        ws.send(
          JSON.stringify({
            id: params.id,
            username: canvasState.username,
            method: "CONNECTION",
          })
        );
      };
      ws.onmessage = (event) => {
        let msg = JSON.parse(event.data);
        switch (msg.method) {
          case "CONNECTION":
            console.log(`username ${msg.username} connected`);
            break;
          case "DRAW":
            drawHandler(msg);
            break;
        }
      };
    }
  }, [canvasState.username]);

  const drawHandler = () => {};

  const mouseDownHandler = () => {
    canvasState.pushToUndo(canvasRef.current.toDataURL());
  };

  const connectionHandler = () => {
    canvasState.setUsername(usernameInput.current.value);
    setVisibleModal(false);
  };

  return (
    <div className="canvas">
      <Modal show={visibleModal} onHide={() => {}}>
        <Modal.Header>
          <Modal.Title>Type your NickName</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <input ref={usernameInput} />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => connectionHandler()}>
            Log in
          </Button>
        </Modal.Footer>
      </Modal>
      <canvas
        onMouseDown={() => mouseDownHandler()}
        ref={canvasRef}
        width={1000}
        height={700}
      ></canvas>
    </div>
  );
});

export default Canvas;
